import React from "react";
import ReactDOM from "react-dom/client";
import Data from "./Data.js"
import "./style.css"
const Boxes = () =>
{
  const [data_array,setDataArray] = React.useState(Data);
  const Box = (props) =>
  {
    const bg_color = {
      backgroundColor : props.on ? "white" : "black"
    }
    return <div className="box" style={bg_color} onClick = {()=>callBack(props.id)}></div>
  }
  function callBack(id)
  {
    setDataArray(function(previous_array)
    {
      const new_array = [...previous_array];
      new_array[id-1].on = !new_array[id-1].on;
      return new_array; 
    })
  }
  const things_array = data_array.map((arg)=><Box id = {arg.id} on = {arg.on} key = {arg.id} callBack = {callBack}/>);
  return things_array;
}
const Element = <>
  <Boxes/>
</>
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(Element);
