import React from "react";
import ReactDOM from "react-dom";
import Data from "./Data.js"
import "./style.css"
const Boxes = () =>
{
  const Box = (props) =>
  {
    const [color, setColor] = React.useState(props.on);
    const bg_color = {
      backgroundColor : props.on ? "white" : "black"
    }
    return <div className="box" style={bg_color}></div>
  }
  const thingsArray = Data.map(function(x)
  {
    return <Box on = {x.on}/>
  })
  return thingsArray;
}
const Element = <>
  <Boxes/>
</>
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(Element);
