import React from "react";
import ReactDOM from "react-dom/client";


const Form = () =>
{
  const [form_data,setData] = React.useState({
    first_name : "",
    last_name : "",
    checked_property : true,
    employement : "",
    favC
  });
  function handleChange(event)
  {
    setData(function(prevData)
    {
      const {name,value,type} = event.target
      return {
        ...prevData,
        [name] : type === "checkbox" ? !prevData.checked_property : value
      }
    })
  }

  return (
    <form>
      <input type = "text" placeholder="First Name" onChange={handleChange} value = {form_data.first_name} name = "first_name"/>
      <input type = "text" placeholder="Last Name" onChange={handleChange} name = "last_name" value = {form_data.last_name} />
      <input name = "checked_property" type= "checkbox" id = "checkbox" checked = {form_data.checked_property} onChange = {handleChange}/>
      <label htmlFor="checkbox">Are you friendly?</label>
      <fieldset>
        <input type="radio" id = "unemployed" name="employement" value = "unemployed" checked = {form_data.employement === "unemployed"} onClick={handleChange}/>
        <label htmlFor="unemployed">unemployed</label>
        <input type="radio" id = "part-time" name="employement" value = "part-time" checked = {form_data.employement === "part-time"} onClick={handleChange}/>
        <label htmlFor="part-time">Part-times</label>
        <input type="radio" id = "full-time" name="employement" value = "full-time" checked = {form_data.employement === "full-time"} onClick={handleChange}/>
        <label htmlFor="full-time">full-times</label>
      </fieldset>
      <label htmlFor="color">What color do you like</label>
      <br/>
      <select id = "color">
        <option>Red</option>
        <option>Blue</option>
        <option>Yellow</option>
        <option>orange</option>
      </select>
    </form>
  )
}



const Element = <>
  <Form/>

</>
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(Element)
