import React from "react";
import ReactDOM from "react-dom/client";


const Form = () =>
{
  const [form_data,setData] = React.useState({
    first_name : "",
    last_name : "",
    comments : ""
  });
  console.log(form_data.first_name,form_data.last_name);
  function handleChange(event)
  {
    setData(function(prevData)
    {
      const [name,value] = prevData.target
      return {
        ...prevData,
        [name] : value
      }
    })
  }

  return (
    <form>
      <input type = "text" placeholder="First Name" onChange={handleChange} value = {form_data.first_name} name = "first_name"/>
      <input type = "text" placeholder="Last Name" onChange={handleChange} name = "last_name" value = {form_data.last_name} />
      <textarea value={form_data.comments} onChange = {handleChange} name = "comments"/>
      <input type= "checkbox" id = "checkbox"/>
      <label htmlFor="checkbox">Are you friendly?</label>
    </form>
  )
}



const Element = <>
  <Form/>

</>
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(Element)
