import React from "react";
import ReactDOM from "react-dom/client";
import "./style.css"


const Form = () =>
{
  const [form_data,setFormData] = React.useState({
    email : "",
    password : "",
    confirm_password : "",
    checked : false
  })
  function handleChange(event)
  {
    const {name,value,type} = event.target
    setFormData(function(prev_form)
    {
      return {
        ...prev_form,
        [name] : type === "checkbox" ? !prev_form.checked : value
      }
    })
  }
  function handleSubmit(event) {
    event.preventDefault()
    form_data.confirm_password === form_data.password ? console.log("Thanks for singing in") : console.log("passwords don't match")
     && form_data.checked && console.log("Thanks for signing up for our newsletter")
  }
return (
    <div className="form-container">
        <form className="form" onSubmit={handleSubmit}>
            <input 
                type="email" 
                placeholder="Email address"
                className="form--input"
                name = "email"
                value={form_data.email}
                onChange = {handleChange}
            />
            <input 
                type="password" 
                placeholder="Password"
                className="form--input"
                name = "password"
                value={form_data.password}
                onChange = {handleChange}
            />
            <input 
                type="password" 
                placeholder="Confirm password"
                className="form--input"
                name="confirm_password"
                value={form_data.confirm_password}
                onChange = {handleChange}
            />

            <div className="form--marketing">
                <input
                    id="okayToEmail"
                    type="checkbox"
                    name="checked"
                    checked = {form_data.checked}
                    onChange = {handleChange}
                />
                <label htmlFor="okayToEmail">I want to join the newsletter</label>
            </div>
            <button 
                className="form--submit"
            >
                Sign up
            </button>
        </form>
    </div>
)
}



const Element = <>
  <Form/>

</>
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(Element)
