import React from "react";
import ReactDom from 'react-dom';
import cr7 from "cr7.jpeg";
import react from "./learn_react.jpeg";
import bikes from "./biking.jpeg"

const Data =  [
  {
      id: 1,
      title: "Life Lessons with CR7",
      description: "I will share with you what I call \"Positively Impactful Moments of Disappointment.\" Throughout my career, many of my highest moments only came after setbacks and losses. But learning from those difficult moments is what gave me the ability to rise above them and reach my goals.",
      price: 136,
      coverImg: {cr7},
      stats: {
          rating: 5.0,
          reviewCount: 6
      },
      location: "Online",
      openSpots: 0,
  },
  {
      id: 2,
      title: "Learn React",
      description: "Interested in becoming a React Developer? For beginner and experienced developers alike, join us",
      price: 125,
      coverImg: {react},
      stats: {
          rating: 5.0,
          reviewCount: 30
      },
      location: "Online",
      openSpots: 27,
  },
  {
      id: 3,
      title: "Group Mountain Biking",
      description: "Experience the beautiful Norwegian landscape and meet new friends all while conquering rugged terrain on your mountain bike. (Bike provided!)",
      price: 50,
      coverImg: {bikes},
      stats: {
          rating: 4.8,
          reviewCount: 2
      },
      location: "Norway",
      openSpots: 3,
  }
]


const Card = (props) => {
  return (
    <>
      <img src={props.coverImg}/>
      <p>{props.stats.rating}({props.stats.reviewCount}).{props.location}</p>
      <h2>{props.title}</h2>
      <strong>From ${props.price} / Person</strong>

    </>
  )
}




const array = Data.map(function(x)
{
  return (
    <Card  id = {x.id} title = {x.title} description = {x.description}
    price = {x.price} coverImg = {x.coverImg} stats = {x.stats} location = {x.location}
    openSpots  = {x.openSpots}/>
  )
})


const Element = <>
  {array}
</>




const root = ReactDom.createRoot(document.getElementById('root'));
root.render(Element)
