import React from "react";
import ReactDOM from "react-dom/client";
import Data from "./Data.js"
import "./style.css"
const Boxes = () =>
{
  const [boxes,setBoxes] = React.useState(Data);
  const Box = (props) =>
  {
    const bg_color = {
      backgroundColor : props.on ? "white" : "black"
    }
    return <div className="box" style={bg_color} onClick = {() => props.callBack(props.id)}></div>
  }
  function callBack(id)
  {
    setBoxes(function(x)
    {
      const y = [...x];
      y[id-1].on = !y[id-1].on 
      return y;
    })
  }
  const thingsArray = Data.map(function(x)
  {
    return <Box on = {x.on} callBack = {callBack} key = {x.id} id = {x.id}/>
  })
  return thingsArray;
}
const Element = <>
  <Boxes/>
</>
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(Element);
